// Extract and validate AI response
const input = $('Parse Input').first().json;
const groqResponse = $input.first().json;

try {
  const aiContent = groqResponse.choices[0].message.content;
  const parsed = JSON.parse(aiContent);

  return {
    json: {
      ok: true,
      original_text: input.text,
      parsed: {
        date: parsed.date,
        time: parsed.time,
        day_name: parsed.day_name || '',
        is_valid: parsed.is_valid !== false,
        confidence: parsed.confidence || 0.5,
        reason: parsed.reason || null
      },
      tenant_id: input.tenantId,
      tokens_used: groqResponse.usage?.total_tokens || 0
    }
  };
} catch (e) {
  return {
    json: {
      ok: false,
      original_text: input.text,
      error: 'Could not parse date from text',
      raw_response: groqResponse.choices?.[0]?.message?.content || 'No response'
    }
  };
}
