parameters.jsCode:
// Parse Input
const webhookData = $input.first().json;
const input = webhookData.body || webhookData;

const message = input.message || input.text || '';
const tone = input.tone || 'friendly'; // friendly, professional, casual
const salonName = input.salon_name || 'our salon';
const tenantId = input.tenant_id || webhookData.headers?.['x-tenant-id'] || null;

if (!message) {
  throw new Error('message text is required');
}

return {
  json: { message, tone, salonName, tenantId }
};

