parameters.jsCode:
// Format AI response
const input = $('Parse Input').first().json;
const groqResponse = $input.first().json;

try {
  const aiContent = groqResponse.choices[0].message.content;
  const parsed = JSON.parse(aiContent);

  return {
    json: {
      ok: true,
      original: input.message,
      polished: parsed.polished || aiContent,
      char_count: parsed.char_count || (parsed.polished || aiContent).length,
      segment_count: parsed.segment_count || 1,
      tone: input.tone,
      tokens_used: groqResponse.usage?.total_tokens || 0
    }
  };
} catch (e) {
  return {
    json: {
      ok: true,
      original: input.message,
      polished: groqResponse.choices?.[0]?.message?.content || input.message,
      tone: input.tone
    }
  };
}
