parameters.jsCode:
// Parse Input
const webhookData = $input.first().json;
const input = webhookData.body || webhookData;

const notes = input.notes || input.text || '';
const clientName = input.client_name || 'Client';
const tenantId = input.tenant_id || webhookData.headers?.['x-tenant-id'] || null;

if (!notes) {
  throw new Error('notes text is required');
}

return {
  json: { notes, clientName, tenantId }
};

