parameters.jsCode:
// Format AI response
const input = $('Parse Input').first().json;
const groqResponse = $input.first().json;

try {
  const aiContent = groqResponse.choices[0].message.content;
  const parsed = JSON.parse(aiContent);

  return {
    json: {
      ok: true,
      client_name: input.clientName,
      summary: parsed.summary || aiContent,
      alerts: parsed.alerts || [],
      mood: parsed.mood || 'neutral',
      original_length: input.notes.length,
      tokens_used: groqResponse.usage?.total_tokens || 0
    }
  };
} catch (e) {
  // If AI doesn't return JSON, use raw text as summary
  return {
    json: {
      ok: true,
      client_name: input.clientName,
      summary: groqResponse.choices?.[0]?.message?.content || 'Unable to summarize',
      alerts: [],
      mood: 'neutral'
    }
  };
}
